import React, { useState, useEffect } from 'react';
import RoommateManager from './components/RoommateManager';
import ChoreManager from './components/ChoreManager';
import AssignmentDisplay from './components/AssignmentDisplay';
import ShoppingListManager from './components/ShoppingListManager';
import RequestManager from './components/RequestManager';
import LaundryScheduler from './components/LaundryScheduler';
import CalendarSettings from './components/CalendarSettings';
import AuthSetup from './components/AuthSetup';
import GoogleLoginButton from './components/GoogleLoginButton';
import UserProfile from './components/UserProfile';
import RoommateSelector from './components/RoommateSelector';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { stateAPI } from './services/api';
import './App.css';

// Main App component that uses auth context
function MainApp() {
  const [activeTab, setActiveTab] = useState('assignments');
  const [appStatus, setAppStatus] = useState('loading');
  const [error, setError] = useState(null);
  const { 
    isAuthenticated, 
    user, 
    needsRoommateLink, 
    isLoading: authLoading,
    isConfigured 
  } = useAuth();

  useEffect(() => {
    checkAppHealth();
  }, []);

  const checkAppHealth = async () => {
    try {
      await stateAPI.health();
      setAppStatus('ready');
      setError(null);
    } catch (err) {
      setAppStatus('error');
      setError('Unable to connect to the backend server. Make sure the Flask server is running on port 5000.');
    }
  };

  const tabs = [
    { id: 'assignments', label: 'Assignments', icon: '📋' },
    { id: 'roommates', label: 'Roommates', icon: '👥' },
    { id: 'chores', label: 'Chores', icon: '🧹' },
    { id: 'laundry', label: 'Laundry', icon: '🧺' },
    { id: 'shopping', label: 'Shopping List', icon: '🛒' },
    { id: 'requests', label: 'Requests', icon: '🙋' },
    { id: 'calendar', label: 'Calendar Settings', icon: '📅' },
    { id: 'auth', label: 'Authentication', icon: '🔐' },
  ];

  const renderActiveComponent = () => {
    switch (activeTab) {
      case 'roommates':
        return <RoommateManager />;
      case 'chores':
        return <ChoreManager />;
      case 'laundry':
        return <LaundryScheduler />;
      case 'shopping':
        return <ShoppingListManager />;
      case 'requests':
        return <RequestManager />;
      case 'calendar':
        return <CalendarSettings />;
      case 'auth':
        return <AuthSetup />;
      case 'assignments':
      default:
        return <AssignmentDisplay />;
    }
  };

  if (appStatus === 'loading') {
    return (
      <div className="app-loading">
        <div className="loading-content">
          <h2>🏠 RoomieRoster</h2>
          <p>Loading application...</p>
        </div>
      </div>
    );
  }

  if (appStatus === 'error') {
    return (
      <div className="app-error">
        <div className="error-content">
          <h2>🏠 RoomieRoster</h2>
          <div className="error-message">
            <h3>Connection Error</h3>
            <p>{error}</p>
            <button onClick={checkAppHealth} className="button primary">
              Retry Connection
            </button>
            <div className="help-text">
              <p>To start the backend server:</p>
              <code>cd backend && python app.py</code>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <div className="header-left">
            <h1 className="app-title">
              🏠 <span>RoomieRoster</span>
            </h1>
            <p className="app-subtitle">Household Chore Management Made Easy</p>
          </div>

          <div className="header-right">
            {isAuthenticated ? (
              <UserProfile compact={true} showRoommateInfo={true} showActions={true} />
            ) : isConfigured ? (
              <GoogleLoginButton />
            ) : (
              <div className="auth-setup-prompt">
                <button 
                  onClick={() => setActiveTab('auth')} 
                  className="button secondary small"
                >
                  Setup Authentication
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      <nav className="app-nav">
        <div className="nav-tabs">
          {tabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`nav-tab ${activeTab === tab.id ? 'active' : ''}`}
            >
              <span className="tab-icon">{tab.icon}</span>
              <span className="tab-label">{tab.label}</span>
            </button>
          ))}
        </div>
      </nav>

      <main className="app-main">
        <div className="main-content">
          {renderActiveComponent()}
        </div>
      </main>

      <footer className="app-footer">
        <p>RoomieRoster - Fair chore distribution for happy households</p>
      </footer>

      {/* Roommate Linking Modal */}
      {needsRoommateLink && (
        <div className="modal-overlay">
          <div className="modal-content">
            <RoommateSelector />
          </div>
        </div>
      )}
    </div>
  );
}

// Root App component with AuthProvider
function App() {
  return (
    <AuthProvider>
      <MainApp />
    </AuthProvider>
  );
}

export default App;
